	private void paramListValueChanged(javax.swing.event.ListSelectionEvent evt) {//GEN-FIRST:event_paramListValueChanged
		// Add your handling code here:
		if (paramList.getSelectedIndex() != -1) {
			paramValue.setText((String)paramList.getSelectedValue());
		}
	}//GEN-LAST:event_paramListValueChanged
	
	private void cmdRemoveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmdRemoveActionPerformed
		int selectedIndex = paramList.getSelectedIndex();
		if (selectedIndex != -1) {
			javax.swing.DefaultListModel dlm = (DefaultListModel)paramList.getModel();
			dlm.removeElementAt(selectedIndex);
			paramList.setSelectedIndex(selectedIndex-1);
		}
	}//GEN-LAST:event_cmdRemoveActionPerformed
	
	private void cmdEditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmdEditActionPerformed
		// Add your handling code here:
		int selectedIndex = paramList.getSelectedIndex();
		if (selectedIndex != -1) {
			javax.swing.DefaultListModel dlm = (DefaultListModel)paramList.getModel();
			dlm.setElementAt(paramValue.getText(), selectedIndex);
		}
	}//GEN-LAST:event_cmdEditActionPerformed
	
	private void cmdAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmdAddActionPerformed
		javax.swing.DefaultListModel dlm = (DefaultListModel)paramList.getModel();
		dlm.addElement(paramValue.getText());
	}//GEN-LAST:event_cmdAddActionPerformed
	
	
