	public String[] getParameters() {
		javax.swing.DefaultListModel dlm = (DefaultListModel)paramList.getModel();
		String[] rv = new String[dlm.getSize()];
		for (int i=0; i < dlm.getSize(); i++) {
			rv[i] = (String)dlm.get(i);
		}
		return rv;
	}
	public void setParameters(String[] parameters) {
		javax.swing.DefaultListModel dlm = (DefaultListModel)paramList.getModel();
		for (int i=0; parameters != null && i < parameters.length; i++) {
			dlm.addElement(parameters[i]);
		}
	}
